package com.androidlessons.recyclerviewpractice;

import androidx.appcompat.app.AppCompatActivity;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import android.os.Bundle;
import android.util.Log;

import java.util.ArrayList;

public class MainActivity extends AppCompatActivity {
    private static final String TAG = "MainActivity";

    private ArrayList<String> mNames = new ArrayList<>();
    private ArrayList<String> mImageUrls = new ArrayList<>();

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);
        Log.d(TAG, "onCreate: started.");
        initBitmaps();



    }

    private void initBitmaps(){
        Log.d(TAG, "initImageBitmaps: preparing bitmaps.");

       mImageUrls.add("https://www.kkhotels.com/wp-content/uploads/2020/01/Paris-City-Eiffeltower-View.jpg");
       mNames.add("Paris");

       mImageUrls.add("data:image/jpeg;base64,");
       mNames.add("Pyramids of Giza");

       mImageUrls.add("data:image/jpeg;base64,");
       mNames.add("London");

       mImageUrls.add("data:image/jpeg;base64,");
       mNames.add("Niagara Falls");

       mImageUrls.add("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT-fBVok1ViVVPrMSXnpXxnu2VwYr4RvVTA5g&usqp=CAU");
       mNames.add("Christ statue in Brazil");

       mImageUrls.add("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRT0D7D4wkeAfHT7uHIh9PdO68M2Szx8Bve0w&usqp=CAU");
       mNames.add("Kyoto");


       initRecyclerView();


    }

    private void initRecyclerView(){
        Log.d(TAG, "iniRecyclerView: initRecyclerView.");
        RecyclerView recyclerView = findViewById(R.id.recycler_view);
        RecyclerViewAdapter adapter = new RecyclerViewAdapter(mNames, mImageUrls, this);
        recyclerView.setAdapter(adapter);
        recyclerView.setLayoutManager(new LinearLayoutManager(this));

    }

}